import React, { useState } from 'react'
import './Navbar.css'
import underline from '../../assets/nav_underline.svg'
import AnchorLink from 'react-anchor-link-smooth-scroll';

export const Navbar = () => {

  let [menu,setMenu] = useState('home');

  return (
    <div className='navbar'>
        <img src='Sid_website.png' alt="" />
        <ul className="nav-menu">
            <li><AnchorLink className=''></AnchorLink><p onClick ={()=>setMenu('about')}>About Me</p>{menu==='about'?<img src={underline}/>:<></>}</li>
            <li><AnchorLink className=''></AnchorLink><p onClick ={()=>setMenu('home')}>Home</p>{menu==='home'?<img src={underline}/>:<></>}</li>
            <li><AnchorLink className=''></AnchorLink><p onClick ={()=>setMenu('services')}>Services</p>{menu==='services'?<img src={underline}/>:<></>}</li>
            <li><AnchorLink className=''></AnchorLink><p onClick ={()=>setMenu('work')}>Portfolio</p>{menu==='work'?<img src={underline}/>:<></>}</li>
            <li><AnchorLink className=''></AnchorLink><p onClick ={()=>setMenu('contact')}>Contact</p>{menu==='contact'?<img src={underline}/>:<></>}</li>
        </ul>
        <div className="nav-connect">Connect With Me</div>
    </div>
  )
}
